import { onMounted, ref, nextTick } from 'vue';
import NodeTemplate from '@/components/Board/Nodes/NodeTemplate.vue';
export default {
  __name: 'NodeAdd',
  props: {
    editor: {
      type: Object
    }
  },
  emits: ["addNode", "helloWorld"],

  setup(__props, {
    expose,
    emit
