    expose();
    const props = __props;
    const values = {
      'name': 'add',
      'inputs': 2,
      'outputs': 1,
      'node': 'nodeAdd'
    };
    let node = ref(null);
    let id = ref(0);
    const variableName = ref();

    const addNode = () => emit('addNode', values);

