        'js': `var ${variableName.value} = ${input1} + ${input2};`,
        'output': `${variableName.value}'`
      });
    };

    onMounted(async () => {
      await nextTick();
      id = node.value.parentElement.parentElement.parentElement.id.split('-')[1];
    });
    const __returned__ = {
      values,
      props,
      node,
      id,
      variableName,
      emit,
      addNode,
      helloWorld,
      updateData,
      onMounted,
      ref,
      nextTick,
      NodeTemplate
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
