export default {
  __name: 'CodeVisualizer',
  props: {
    pythonCode: {
      type: String,
      required: true
    }
  },
  emits: ["close"],

  setup(__props, {
    expose,
    emit
  }) {
    expose();
    const props = __props;

    const close = () => emit('close');

    const __returned__ = {
      emit,
      props,
      close
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
