import { inject } from 'vue';
import FlowSelector from './FlowSelector.vue';
export default {
  __name: 'AdminPanel',
  props: {
    databaseFlows: {
      type: Object
    }
  },
  emits: ["close"],

  setup(__props, {
    expose,
    emit
  }) {
    expose();
    const props = __props;
    const editor = inject('editor');
    const flowId = inject('flowId');
    const flowTitle = inject('flowTitle');

    const close = () => emit('close');

    const loadFlow = (id, title, content) => {
      flowId.value = id;
      let data = content;
      data = data.replaceAll('closeSquare', '}');
      data = data.replaceAll('openSquare', '{');
      data = data.replaceAll('quote', '"');
      data = data.replace(/[\r\n]/gm, '');
      editor.value.import(JSON.parse(data));
      emit('close');
    };

    const __returned__ = {
      emit,
      editor,
      flowId,
      flowTitle,
      close,
      props,
      loadFlow,
      inject,
      FlowSelector
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
